            $feed['coalizioni'][$idCoalizione]=array("denominazione"=>$curCoalizione->GetProp("denominazione"),"image"=>$curCoalizioneImagePath,"presidente"=>$curCoalizione->GetProp("nome_candidato"),"cv"=>$curCv,"cg"=>$curCg);
