                        $feed["comuni"][$idComune]['risultati']['voti_candidato'][$id]['circoscrizione']=$candidato->GetProp("circoscrizione");
                    }
                }
            }

            //dati generali
            $feed['stats']['regionale']['sezioni']+=$curComune->GetProp('sezioni');
