                "risultati"=>array(
                    "sezioni_scrutinate"=>0,
                    "votanti_m"=>0,
                    "votanti_f"=>0,
                    "votanti_tot"=>0,
                    "votanti_percent"=>0,
                    "voti_presidente"=>array(
                        "sezioni_scrutinate"=>0,
                        "voti_tot"=>0,
                        "consolidato"=>1),
                    "voti_lista"=>array(
                        "sezioni_scrutinate"=>0,
                        "voti_tot"=>0,
                        "consolidato"=>1),
                    "voti_candidato"=>array(
                        "sezioni_scrutinate"=>0,
                        "voti_tot"=>0,
                        "consolidato"=>1))
                );
            
            $feed['stats']['circoscrizionale'][$curComune->GetProp('id_circoscrizione')]['sezioni']+=intVal($curComune->GetProp('sezioni'));
