                        else  
                        {
                            if($giornataValues['ore_19']['count']>0) $feed['stats']['regionale']['affluenza'][$giornata]['ore_22']['count']+=$giornataValues['ore_19']['count'];
                            else $feed['stats']['regionale']['affluenza'][$giornata]['ore_22']['count']+=$giornataValues['ore_12']['count'];
