                    }
                    else
                    {
                        if(strpos($curMsg,$curComune->GetProp("denominazione"))!==false && strpos($curMsg,"Modifica comune") !==false && $aggiornamento == "non aggiornato")
                        {
                            $aggiornamento=$log[$key]['data'];
                        }
